function split(delimiter, text)	local list = {}	local pos = 1	while 1 do		local first,last = string.find(text, delimiter, pos)		if first then			table.insert(list, string.sub(text, pos, first-1))			pos = last+1		else			table.insert(list, string.sub(text, pos))			break		end	end	return listend-- Retreive the entry that has thisfunction table.find(t, sFind)	for _, v in pairs(t) do		if v == sFind then			return _		end	end	return nilend